// TopBar.js
import React, { useEffect, useState } from 'react';
import './TopBar.css';

const TopBar = () => {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) { // Пороговое значение для изменения стиля
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);

    // Удаляем обработчик события при размонтировании компонента
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div id="topbar" className={`d-flex align-items-center fixed-top shadow-sm ${scrolled ? 'topbar-scrolled' : ''}`}>
      <div className="container d-flex justify-content-center justify-content-md-between">
        <div className="contact-info d-flex align-items-center">
          <i className="bi bi-phone d-flex align-items-center"><span>+1 5589 55488 55</span></i>
          <i className="bi bi-clock d-flex align-items-center ms-4"><span> Mon-Sat: 11AM - 23PM</span></i>
        </div>
        <div className="languages d-none d-md-flex align-items-center">
          <ul className="d-flex">
            <li><a href="#" className="active">En</a></li>
            <li><a href="#">De</a></li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default TopBar;
