import React, { useState } from 'react';
import './Menu.css'; // Не забудьте додати відповідні стилі для вашого меню
import breadBarrelImage from '../img/menu/bread-barrel.jpg';



const Menu = () => {

  return (
    <>
      {/* Menu Section */}
      <section id="menu" className="menu section-bg">
        <div className="container" data-aos="fade-up">
          <div className="section-title">
            <h2>Menu</h2>
            <p>Check Our Tasty Menu</p>
          </div>

          <div className="row" data-aos="fade-up" data-aos-delay="100">
            <div className="col-lg-12 d-flex justify-content-center">
              <ul id="menu-flters">
                <li
                  onClick={() => handleFilterClick('*')}
                  className={activeFilter === '*' ? 'filter-active' : ''}
                >
                  All
                </li>
                <li
                  onClick={() => handleFilterClick('.filter-starters')}
                  className={activeFilter === '.filter-starters' ? 'filter-active' : ''}
                >
                  Starters
                </li>
                <li
                  onClick={() => handleFilterClick('.filter-salads')}
                  className={activeFilter === '.filter-salads' ? 'filter-active' : ''}
                >
                  Salads
                </li>
                <li
                  onClick={() => handleFilterClick('.filter-specialty')}
                  className={activeFilter === '.filter-specialty' ? 'filter-active' : ''}
                >
                  Specialty
                </li>
              </ul>
            </div>
          </div>

          <div className="row menu-container" data-aos="fade-up" data-aos-delay="200">
            {/* Menu Items */}
            <MenuItem
                imgSrc={breadBarrelImage} // Це правильно
                name="Bread Barrel"   
                price="$6.95"
                ingredients="Lorem, deren, trataro, filede, nerada"
                filterClass="filter-specialty"
                activeFilter={activeFilter}
            />

            <MenuItem
              imgSrc='../../img/menu/bread-barrel.jpg'
              name="Bread Barrel"   
              price="$6.95"
              ingredients="Lorem, deren, trataro, filede, nerada"
              filterClass="filter-specialty"
              activeFilter={activeFilter}
            />
            {/* Додайте інші MenuItem компоненти за аналогією */}
          </div>
        </div>
      </section>

      {/* Specials Section */}
      <section id="specials" className="specials">
        <div className="container" data-aos="fade-up">
          <div className="section-title">
            <h2>Specials</h2>
            <p>Check Our Specials</p>
          </div>

          <div className="row" data-aos="fade-up" data-aos-delay="100">
            <div className="col-lg-3">
              <ul className="nav nav-tabs flex-column">
                <li className="nav-item">
                  <a
                    className={`nav-link ${activeTab === 'tab-1' ? 'active show' : ''}`}
                    onClick={() => handleTabClick('tab-1')}
                  >
                    Modi sit est
                  </a>
                </li>
                {/* Додайте інші вкладки за аналогією */}
              </ul>
            </div>

            <div className="col-lg-9 mt-4 mt-lg-0">
              <div className="tab-content">
                <TabPane
                  id="tab-1"
                  activeTab={activeTab}
                  imgSrc="assets/img/specials-1.png"
                  title="Architecto ut aperiam autem id"
                  description="Qui laudantium consequatur laborum sit qui ad sapiente dila parde sonata raqer a videna mareta paulona marka"
                />
                {/* Додайте інші TabPane компоненти за аналогією */}
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

const MenuItem = ({ imgSrc, name, price, ingredients, filterClass, activeFilter }) => {
  return (
    <div className={`col-lg-6 menu-item ${filterClass} ${activeFilter === '*' || activeFilter === filterClass ? '' : 'd-none'}`}>
      <img src={imgSrc} className="menu-img" alt={name} />
      <div className="menu-content">
        <a href="#">{name}</a>
        <span>{price}</span>
      </div>
      <div className="menu-ingredients">
        {ingredients}
      </div>
    </div>
  );
};

const TabPane = ({ id, activeTab, imgSrc, title, description }) => {
  return (
    <div className={`tab-pane ${activeTab === id ? 'active show' : ''}`} id={id}>
      <div className="row">
        <div className="col-lg-8 details order-2 order-lg-1">
          <h3>{title}</h3>
          <p className="fst-italic">{description}</p>
          <p>{description}</p>
        </div>
        <div className="col-lg-4 text-center order-1 order-lg-2">
          <img src={imgSrc} alt={title} className="img-fluid" />
        </div>
      </div>
    </div>
  );
};

export default Menu;
